    private void btnShowMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnShowMouseClicked
        if (lblChanged.isVisible())
        {
            lblChanged.setVisible(false);
        }
        else
        {
            lblChanged.setVisible(true);
        }
    }//GEN-LAST:event_btnShowMouseClicked

    private void btnPwdMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnPwdMouseClicked
        lblChanged.setText(String.valueOf(pwdUser.getPassword()));
    }//GEN-LAST:event_btnPwdMouseClicked

