    private void cbEmailsMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_cbEmailsMouseClicked
        if (cbEmails.isSelected())
            lblCheckBox.setText("Send Email");
        else
            lblCheckBox.setText("Don't Send email");
    }//GEN-LAST:event_cbEmailsMouseClicked

