    private void jButton3MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton3MouseClicked
        if (rbMale.isSelected())
            lblRadioButton.setText("Male");
        else if (rbFemale.isSelected())
            lblRadioButton.setText("Female");
        else
            lblRadioButton.setText("");
    }//GEN-LAST:event_jButton3MouseClicked

    private void btnComboMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnComboMouseClicked
        String sValue = "";
        
        if (((String)cmbStates.getSelectedItem()).equalsIgnoreCase("co"))
            btnCombo.setText("Colorado");
        else if (((String)cmbStates.getSelectedItem()).equalsIgnoreCase("tx"))
            btnCombo.setText("Texas");
        else if (((String)cmbStates.getSelectedItem()).equalsIgnoreCase("ut"))
            btnCombo.setText("Utah");
        else if (((String)cmbStates.getSelectedItem()).equalsIgnoreCase("wy"))
            btnCombo.setText("Wyoming");
    }//GEN-LAST:event_btnComboMouseClicked

    private void btnStateMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnStateMouseClicked
        btnState.setText((String)lstState.getSelectedValue());
    }//GEN-LAST:event_btnStateMouseClicked

